                    {
                        Work.map( d => 
                        <Card key={d.id} data={d} />
                        )
                    }
                </Main>
                <BigTitlte text="WORK" top='10%' right="20%" />


            </Box>
        </ThemeProvider>
    );
}

export default WorkPage;
